import React from 'react';
import './App.css';

function App() {
  const imgRef = React.createRef();
  let click = 0;
  const leftClick = () => {
    window.scrollBy({
      left: click - 1200,
      behaviour: 'smooth'
    })
  }
  const rightClick = () => {
    console.log('rightclick');
    window.scrollBy({
      left: click + 1200,
      behaviour: 'smooth'
   })
  }
  return (
    <div className="app">
      <div>
        <div className="img-back" ref={imgRef} />
        <div onClick={leftClick}>
          <i className="fa fa-arrow-left left-arrow fa-3x"/>
        </div>
        <div onClick={rightClick} >
          <i className="fa fa-arrow-right right-arrow fa-3x"/>
        </div>
      </div>
    </div>
  );
}

export default App;
