import React, {useCallback} from 'react';
import logo from './logo.svg';
import './App.css';

function App() {
  const imgRef = React.createRef();
  const click = 0;
  const leftClick = () => {
    window.scrollBy({
      left: click-3,
      behaviour: 'smooth'
   })
  }
  return (
    <div>
      <div className="cont">
        <div className="img-back" ref={imgRef} />
        <i className="fa fa-arrow-left left-arrow fa-3x"/>
        <i className="fa fa-arrow-right right-arrow fa-3x"/>
      </div>
    </div>
  );
}

export default App;
