import React from 'react';
import './App.css';

function App() {
  const imgRef = React.createRef();
  let click = 0;
  const SmoothVerticalScrolling = (e, time, where) => {
    var eTop = e.getBoundingClientRect().top;
    var eAmt = eTop / 100;
    var curTime = 0;
    while (curTime <= time) {
        window.setTimeout(SVS_B, curTime, eAmt, where);
        curTime += time / 100;
    }
}

 const SVS_B = (eAmt, where) =>{
    if(where == "center" || where == "")
        window.scrollBy(0, eAmt / 2);
    if (where == "top")
        window.scrollBy(0, eAmt);
}
  const leftClick = () => {
    window.scrollBy({
      left: click - 100,
      behaviour: 'smooth'
    })
  }
  const rightClick = () => {
    console.log('rightclick');
    window.scrollBy({
      left: click + 100,
      behaviour: 'smooth'
   })
  }
  return (
    <div className="app">
      <div>
        <div className="img-back" ref={imgRef} />
        <div onClick={SmoothVerticalScrolling(myelement, 275, "center")}>
          <i className="fa fa-arrow-left left-arrow fa-3x"/>
        </div>
        <div onClick={rightClick} >
          <i className="fa fa-arrow-right right-arrow fa-3x"/>
        </div>
      </div>
    </div>
  );
}

export default App;
